// Trigger Beginning 
println 'Trigger started: Auto-approve Expenses, business object: Expenses, event: BeforeInsert';

try {

  // Execute Conditionally Criterion (If Amount <= 200)
  def codeCriterion_1 = (amount <= 200);
  println "Trigger condition (amount <= 200) is " + codeCriterion_1;
  if (codeCriterion_1) {
    // Action Group (Set Approved = true)
    approved = true;

    def recipients = 'sherry.yu@oracle.com';
    def sender = 'nobody@oracle.com';
    def variables = [:];
    variables["Amount"] = String.valueOf({ amount }());
    variables["Category"] = String.valueOf(category);
    variables["Date"] = String.valueOf({ dateIncurred }());
    variables["Description"] = String.valueOf(description);

    def subject = "Your expense of [[Amount]] has been automatically approved.";
    def body = "<p>Your expense has been automatically approved!</p><p>Description:&nbsp;[[Description]]</p><p>Category:&nbsp;[[Category]]</p><p>Amount:&nbsp;[[Amount]]</p><p>Date:&nbsp;[[Date]]</p>";
    com.oracle.breeze.ramp.helpers.Email.send(recipients, sender, subject, body, variables);

    return;
  }
  // Execute Conditionally Criterion (If Amount > 200)
  def codeCriterion_2 = (amount > 200);
  println "Trigger condition (amount > 200) is " + codeCriterion_2;
  if (codeCriterion_2) {
    // Action Group (Send email)
    def recipients = 'sherry.yu@oracle.com';
    def sender = 'nobody@oracle.com';
    def variables = [:];
    variables["Amount"] = String.valueOf({ amount }());
    variables["Category"] = String.valueOf(category);
    variables["Date"] = String.valueOf({ dateIncurred }());
    variables["Description"] = String.valueOf(description);

    def subject = "Your expense of [[Amount]] has been submitted.";
    def body = "<p>Your expense has been submitted and is pending approval.&nbsp;</p><p>Amount:&nbsp;[[Amount]]</p><p>Date:&nbsp;[[Date]]</p><p>Description:&nbsp;[[Description]]</p><p>Category:&nbsp;[[Category]]</p>";
    com.oracle.breeze.ramp.helpers.Email.send(recipients, sender, subject, body, variables);

    return;
  }

} finally {

  // Trigger End 
  println 'Trigger ended: Auto-approve Expenses';

}
